CREATE TABLE Question
(
	QuestionID TEXT PRIMARY KEY,
	QuestionContent TEXT,
	A TEXT,
	B TEXT,
	C TEXT,
	D TEXT,
	Correct TEXT,
	SubjectId INTEGER
);

CREATE TABLE Subject
(
	SubjectId INTEGER PRIMARY KEY,
	SubjectName TEXT,
	Active BIT,
	NumberSentence INTEGER,
	Prefix TEXT
);


